pragma solidity ^0.8.20;

import "./Card.sol";

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

/**
 * @title Booster
 * @dev NFT booster.
 * @notice This contract is used to mint boosters of cards,
 * which are ERC721 tokens mapping to arrays of card ids.
 * It also allows to unpack a booster, which burns the booster
 * and transfers the cards to the caller.
 * The provided Card contract is assumed to has this contract
 * approved to transfer cards from the caller of minting functions.
 */
contract Booster is ERC721URIStorage, Ownable, ERC721Holder {
    // Booster id counter
    uint256 private _idCounter = 0;
    // Booster id => array of card ids
    mapping(uint256 => uint256[]) private _boosterCards;
    // Card contract
    IERC721 private _cardContract;

    /**
     * @notice Constructor
     * @param cardContract Card contract address
     */
    constructor(
        IERC721 cardContract
    ) ERC721("Booster", "BST") Ownable(_msgSender()) {
        _cardContract = cardContract;
    }

    /**
     * @notice Mint a booster of cards from `cardIds` for `_to`
     * while setting `_boosterUri` as its metadata URI.
     * Assume that the caller has already approved this contract to transfer the cards
     * owned by the caller.
     * @param _to   Address to mint to
     * @param boosterUri  Booster metadata URI
     * @param cardIds  Array of card ids to add to the booster
     * @return Booster id
     */
    function mint(
        address _to,
        string memory boosterUri,
        uint256[] memory cardIds
    ) external onlyOwner returns (uint256) {
        require(cardIds.length > 0, "Booster: must contain at least one card");
        uint256 boosterNumber = _idCounter;
        _idCounter++;

        _safeMint(_to, boosterNumber);
        _setTokenURI(boosterNumber, boosterUri);

        for (uint256 i = 0; i < cardIds.length; i++) {
            _boosterCards[boosterNumber].push(cardIds[i]);
            _cardContract.safeTransferFrom(
                _msgSender(),
                address(this),
                cardIds[i]
            );
        }

        return boosterNumber;
    }

    /**
     * @notice Get the array of card ids of a booster.
     * @param boosterId  Booster id
     * @return Array of card ids
     */
    function getCards(
        uint256 boosterId
    ) external view returns (uint256[] memory) {
        require(owner() == _msgSender() ||
            ownerOf(boosterId) == _msgSender(),
            "Booster: caller is not the owner of the booster or the owner of the contract"
        );
        require(_ownerOf(boosterId) != address(0), "Booster: booster does not exist");

        return _boosterCards[boosterId];
    }

    /**
     * @notice Unpack a booster of cards for the caller 
     * if the caller is the owner of the booster, by 
     * burning the booster and transferring the cards.
     * @param boosterId  Booster id
     */
    function unpack(uint256 boosterId) external {
        require(
            ownerOf(boosterId) == _msgSender(),
            "Booster: caller is not the owner of the booster"
        );

        uint256[] memory cardIds = _boosterCards[boosterId];
        for (uint256 i = 0; i < cardIds.length; i++) {
            _cardContract.safeTransferFrom(
                address(this),
                _msgSender(),
                cardIds[i]
            );
        }

        _burn(boosterId);

        delete _boosterCards[boosterId];
    }
}
","keccak256":"0x031cb6bb043b8d6e0b89ebfa5a9d995f308232cf6e049c626407c0558ebd622d","license":"MIT"},"contracts/Card.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Card
 * @dev NFT card with metadata URI.
 * @notice This contract (based on [ERC721](https://docs.openzeppelin.com/contracts/5.x/api/token/erc721))
 * is used to mint cards and store their metadata URIs
 * according to [ERC721URIStorage](https://docs.openzeppelin.com/contracts/5.x/api/token/erc721#ERC721URIStorage).
 */
contract Card is Ownable, ERC721URIStorage {
    // Card id counter
    uint256 private _idCounter = 0;

    constructor() ERC721("Card", "CRD") Ownable(_msgSender()) {}

    /**
     * @notice Mint a card for the address `_to` 
     * while setting its metadata URI to `cardUri`.
     * @param _to   Address to mint to
     * @param cardUri  Card metadata URI
     * @return Card id
     */
    function mint(
        address _to,
        string memory cardUri
    ) external onlyOwner returns (uint256) {
        uint256 cardNumber = _idCounter;
        _idCounter++;

        _safeMint(_to, cardNumber);
        _setTokenURI(cardNumber, cardUri);

        return cardNumber;
    }
}
","keccak256":"0x41e452ffe79c3666b55f765c6538b5bb68c7c2fba89ceee47e7be8f4dabc7bec","license":"MIT"},"contracts/Collection.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Collection
 * @dev Collection of cards.
 * @notice This contract is used to manage collections (sets) of cards
 * and store their ids.
 */
contract Collection is Ownable {
    using EnumerableSet for EnumerableSet.UintSet;

    /**
     * @dev Collection name
     */
    string public name;
    /**
     * @dev Collection cards ids
     */
    EnumerableSet.UintSet private cardsIds;
    /**
     * @dev Expected number of cards in the collection
     */
    uint public expectedCount;

    /**
     * @notice Constructor
     * @param _name   Collection name
     * @param _expectedCount  Expected number of cards in the collection
     */
    constructor(
        string memory _name,
        uint _expectedCount
    ) Ownable(_msgSender()) {
        name = _name;
        expectedCount = _expectedCount;
    }

    /**
     * @notice Add a card to the collection.
     * @param _cardId   Card id
     */
    function addCard(uint256 _cardId) public onlyOwner {
        require(!cardsIds.contains(_cardId), "Card already in collection");
        require(cardsIds.length() < expectedCount, "Collection is full");
        cardsIds.add(_cardId);
    }

    /**
     * @notice Get all the cards in the collection.
     * @return  Array of card ids
     */
    function getCards() public view returns (uint256[] memory) {
        return cardsIds.values();
    }

    /**
     * @notice Check if a card is in the collection.
     * @param _cardId   Card id
     * @return Whether the card is in the collection
     */
    function hasCard(uint256 _cardId) public view returns (bool) {
        return cardsIds.contains(_cardId);
    }

    /**
     * @notice Get the actual number of cards in the collection.
     * @return  Number of cards in the collection
     */
    function count() public view returns (uint256) {
        return cardsIds.length();
    }
}
","keccak256":"0xda953c2017da5a42b6bf55b8b98bd503e984a15bd742e1e28e6903a2e548038e","license":"MIT"},"contracts/Main.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./Collection.sol";
import "./Booster.sol";
import "./Card.sol";

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

/**
 * @title Main
 * @dev Main contract, used to create collections and mint cards.
 * @notice This contract is used to create collections, mint cards
 * and also used to transfer cards to other addresses,
 * since we assume it's the owner of the Card contract
 * and therefore is the original owner of all cards.
 */
contract Main is AccessControl, ERC721Holder {
    /**
     * @dev Array of all collections
     */
    Collection[] public collections;
    /**
     * @dev Card contract
     */
    Card private _cardContract;
    /**
     * @dev Booster contract
     */
    Booster private _boosterContract;
    /**
     * @dev Mapping of collection names to collections
     */
    mapping(string => Collection) private _collectionsByName;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant COLLECTION_ROLE = keccak256("COLLECTION_ROLE");

    /**
     * @notice Constructor, which takes the address of the Card contract.
     * @param cardContract Card contract address
     */
    constructor(
        address cardContract,
        address boosterContract
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(MINTER_ROLE, _msgSender());
        _grantRole(COLLECTION_ROLE, _msgSender());

        _cardContract = Card(cardContract);
        _boosterContract = Booster(boosterContract);
        _cardContract.setApprovalForAll(boosterContract, true);
    }

    /**
     * @notice Create a new collection of cards.
     * @param _name  Collection name
     * @param _expectedCount  Expected number of cards in the collection
     * @return  The created collection
     */
    function createCollection(
        string calldata _name,
        uint256 _expectedCount
    ) external onlyRole(COLLECTION_ROLE) returns (Collection) {
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(
            address(_collectionsByName[_name]) == address(0),
            "Collection already exists"
        );

        Collection newCollection = new Collection(_name, _expectedCount);
        collections.push(newCollection);
        _collectionsByName[_name] = newCollection;
        return newCollection;
    }

    /**
     * @notice Check if a collection exists.
     * @param _name Collection name
     * @return Whether if the collection exists
     */
    function hasCollection(string calldata _name) external view returns (bool) {
        return address(_collectionsByName[_name]) != address(0);
    }

    /**
     * @notice Get a collection by name.
     * @param _name  Collection name
     * @return  The collection
     */
    function getCollectionByName(
        string calldata _name
    ) external view returns (Collection) {
        Collection collection = _collectionsByName[_name];
        require(address(collection) != address(0), "Collection does not exist");
        return collection;
    }

    /**
     * @notice Get a collection by index.
     * @param _collectionId  Collection id
     * @return The collection
     */
    function getCollectionById(
        uint256 _collectionId
    ) public view returns (Collection) {
        require(
            _collectionId < collections.length,
            "Collection does not exist"
        );
        return collections[_collectionId];
    }

    /**
     * @notice Get all collections available.
     * @return All available collections
     */
    function getCollections() external view returns (Collection[] memory) {
        return collections;
    }

    /**
     * @notice Mint a new card and add it to the provided collection.
     * @return  Card token id
     */
    function mintCardForCollection(
        Collection _collection,
        string memory _cardUri
    ) public onlyRole(MINTER_ROLE) returns (uint256) {
        // Mint a new card and add it to the collection
        uint256 cardId = _cardContract.mint(address(this), _cardUri);
        _collection.addCard(cardId);
        return cardId;
    }

    /**
     * @notice Mint multiple cards and add them to the provided collection.
     * @return  Array of card token ids
     */
    function mintCardsForCollection(
        Collection _collection,
        string[] memory _cardUris
    ) external onlyRole(MINTER_ROLE) returns (uint256[] memory) {
        uint256 count = _cardUris.length;
        uint256[] memory cardIds = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            cardIds[i] = mintCardForCollection(_collection, _cardUris[i]);
        }
        return cardIds;
    }

    /**
     * @notice Transfer a card to an address.
     * @param _to  Address to transfer to
     * @param _cardId  Card id
     */
    function transferCard(address _to, uint256 _cardId) public onlyRole(MINTER_ROLE) {
        _cardContract.safeTransferFrom(address(this), _to, _cardId);
    }

    /**
     * @notice Transfer multiple cards to an address.
     * @param _to  Address to transfer to
     * @param _cardIds  Card ids
     */
    function transferCards(
        address _to,
        uint256[] calldata _cardIds
    ) external onlyRole(MINTER_ROLE) {
        for (uint256 i = 0; i < _cardIds.length; i++) {
            transferCard(_to, _cardIds[i]);
        }
    }

    /**
     * @notice Mint a booster of cards with `_boosterUri` as the token URI
     * and add the cards `_cardIds` to the booster while transferring them
     * to `_to`.
     * @param _to  Address to mint the booster to
     * @param _boosterUri  Booster metadata URI
     * @param _cardIds  Array of card ids to add to the booster
     * @return Booster id
     */
    function mintBooster(
        address _to,
        string calldata _boosterUri,
        uint256[] calldata _cardIds
    ) external onlyRole(MINTER_ROLE) returns (uint256) {
        return _boosterContract.mint(_to, _boosterUri, _cardIds);
    }
}
